function StartGame() {
  document.getElementById("startdiv").style.display = "none";

  for (i = 7 ; i <= 10; ++i) {
    document.getElementById("c"+i).style.left = "200px";
    document.getElementById("c"+i).style.top = (50 + i*30) + "px";
  }
};

