  document.getElementById("startdiv").style.display = "none";
  document.getElementById("startdiv1b").style.display = "none";
  document.getElementById("start2").style.display = "block";
};

function reviewInstructions() {
  document.getElementById("scoreboard").style.display = "none";
  for (var card in allCards) {
    document.getElementById(card).style.display = "none";
  }
  document.getElementById("discard_placeholder").style.display = "none";
  document.getElementById("jackstack_placeholder").style.display = "none";
  document.getElementById("rightpile1_placeholder").style.display = "none";

  document.getElementById("welcometitle").style.display = "none";
  document.getElementById("sb1a").style.display = "none";
  document.getElementById("sb1b").style.display = "none";
  document.getElementById("sb2a").style.display = "none";
  document.getElementById("sb2b").style.display = "none";
  document.getElementById("sb2c").style.display = "block";
  document.getElementById("startdiv").style.display = "block";
  document.getElementById("start2").style.display = "block";
};
function stopReviewingInstructions() {
  document.getElementById("scoreboard").style.display = "block";
  for (var card in allCards) {
    document.getElementById(card).style.display = "block";
  }
  document.getElementById("discard_placeholder").style.display = "block";
  document.getElementById("jackstack_placeholder").style.display = "block";
  document.getElementById("rightpile1_placeholder").style.display = "block";

  document.getElementById("startdiv").style.display = "none";
  document.getElementById("start2").style.display = "none";
};

